import React, { Component } from 'react';

class About extends Component {
    render() {
        return (
            <div className='container-fluid'>
                <hr />
                <h2 className='display-4 about-title'>About Me</h2>
                <hr />
                <div className='m-2 p-3'>
                    <div className='left_about'>
                        <h3>whoami</h3>
                        <p>I am <span id='para_name'>Arghya Sahoo</span>, a student of <span id='para_course'>Computer Science and Engineering</span> at Techno International Newtown. I am currently in 3rd Year. I am always learning new skills and building stuff. I have a decent understanding of programing concepts and I can work on backend development.</p>
                    </div>

                    <div className='right_about'>
                        <h3>My Interests</h3>
                        <p>I am interested in learning new technologies which include Blockchain, Machine Learning, Artificial Intelligence, Cyber Security. Along with these, I can comfortably work in Linux and am extemely interested in Linux.</p>
                    </div>

                    <div className='left_about'>
                        <h3>Why Work with me</h3>
                        <p>I am a hardworking person and have a good knowledge on web development. All the time I want to gather more knowledge and more experience. So I try my best for all the time.</p>
                    </div>
                </div>
            </div>
        );
    }
}

export default About;
