import React, { Component } from 'react';
import LanguageSkills from './language_skills';
import FrontendSkills from './frontend_skills'
import BackendSkills from './backend_skills'
import OtherSkills from './Other_skills';

class Skills extends Component {
    constructor(props) {
        super()

        this.state = {
            option: 0
        }

        this.loadLang = this.loadLang.bind(this);
        this.loadFrontend = this.loadFrontend.bind(this);
        this.loadBackend = this.loadBackend.bind(this);
        this.loadOthers = this.loadOthers.bind(this);
    }

    loadLang() {
        console.log('clicked language');
        this.setState({ option: 1 });
        // return (<LanguageSkills />);
    }

    loadFrontend() {
        this.setState({ option: 2 });
        // return (<FrontendSkills />);
    }

    loadBackend() {
        this.setState({ option: 3 });
        // return (<BackendSkills />);
    }

    loadOthers() {
        this.setState({ option: 4 });
        // return (<OtherSkills />);
    }

    render() {
        return (
            <React.Fragment>
                {this.state.option === 1 ? <LanguageSkills /> : <BackendSkills />}
                <div className='container'>
                    <button onClick={this.loadLang}>Language</button>
                    <button onClick={this.loadFrontend}>Frontend</button>
                    <button onClick={this.loadBackend}>Backend</button>
                    <button onClick={this.loadOthers}>Others</button>
                </div>
            </React.Fragment>
        );
    }
}

export default Skills;
