import React, { Component } from 'react';
import LanguageSkills from './language_skills';
import FrontendSkills from './frontend_skills'
import BackendSkills from './backend_skills'
import OtherSkills from './Other_skills';
import Options from './skill_options';

class Skills extends Component {
    constructor(props) {
        super()
        this.state = {
            "option": 2
        }
    }

    render() {
        const option = this.state.option;

        if (option == 1) {
            return (<LanguageSkills />, <Options option={option} />);
        }
        else if (option == 2) {
            return (<FrontendSkills />, <Options option={option} />);
        }
        else if (option == 3) {
            return (<BackendSkills />, <Options option={option} />);
        }
        else if (option == 4) {
            return (<OtherSkills />, <Options option={option} />);
        }
    }
}

export default Skills;
