import React, { Component } from 'react';
import LanguageSkills from './language_skills';
import FrontendSkills from './frontend_skills'
import BackendSkills from './backend_skills'
import OtherSkills from './Other_skills';
import Options from './skill_options';

class Skills extends Component {
    constructor(props) {
        super()
        this.state = {
            "option": 2
        }
    }

    render() {
        const option = this.state.option;

        if (option == 1) {
            return (<LanguageSkills />,
                <button onClick={this.changeState({ 'option': 1 })}>Language</button>);
        }
        else if (option == 2) {
            return (<FrontendSkills />, <button onClick={this.changeState({ 'option': 2 })}>Language</button>);
        }
        else if (option == 3) {
            return (<BackendSkills />, <button onClick={this.changeState({ 'option': 3 })}>Language</button>);
        }
        else if (option == 4) {
            return (<OtherSkills />, <button onClick={this.changeState({ 'option': 4 })}>Language</button>);
        }

        function changeOption(val) {
            this.setState({ 'option': val });
        }
    }
}

export default Skills;
